        return new Source("{% extends 'base.html.twig' %}

{% block title %}Acceuil{% endblock %}

{% block head %}
    <style>
        div.text-home{
            background-image:url(\"https://images.pexels.com/photos/87841/ivy-plants-leaves-wall-87841.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500\") ;
        }
    </style>
{% endblock %}
{% block body %}

    <nav class=\"navbar navbar-expand-lg navbar-light bg-custom\">
        <a class=\"navbar-brand\" href=\"#\"> Un univers d'activités    |</a>
        <button class=\"navbar-toggler\" type=\"button\" data-toggle=\"collapse\" data-target=\"#navbarNavAltMarkup\" aria-controls=\"navbarNavAltMarkup\" aria-expanded=\"false\" aria-label=\"Toggle navigation\">
            <span class=\"navbar-toggler-icon\"></span>
        </button>
        <div class=\"collapse navbar-collapse\" id=\"navbarNavAltMarkup\">
            <div class=\"navbar-nav\">
                <a class=\"nav-item nav-link active\" href=\"/home\">Acceuil </a>
                <a class=\"nav-item nav-link\" href=\"/activity\">Activités </a>
                {% if is_granted(\"ROLE_ADMIN\") %}
                    <a class=\"nav-item nav-link\" href=\"{{ path('user_index') }}\">Animateurs </a>
                {% endif %}
                {% if app.user %}
                    <a class=\"nav-item nav-link\" href=\"/logout\">Se Déconnecter </a>
                {% else %}
                    <a class=\"nav-item nav-link\" href=\"/login\">Se Connecter </a>
                {% endif %}
            </div>
        </div>
    </nav>
<div class=\"card home\">
    <p class=\"text-center\" style=\"font-size:150%; margin:25px\">Tu ne sais pas quoi faire de tes temps libres ? Alors participe à une de nos activité ! :)</p>
    <div class=\"d-flex justify-content-center\">
        <a class=\"btn btn-success\" href=\"/activity\" role=\"button\" style=\"alignment: center; margin-bottom: 15px\"> rejoindre une activité </a>
    </div>
</div>
{% endblock %}
